const catalogBuyButton=document.querySelectorAll(".catalog-buy-button"),catalogBuySuccessPopup=document.querySelector(".product-added-popup"),catalogPopupCloseButton=catalogBuySuccessPopup.querySelector(".button-close");for(let o=0;o<catalogBuyButton.length;o++)catalogBuyButton[o].addEventListener("click",function(o){o.preventDefault(),catalogBuySuccessPopup.classList.add("modal-show")});catalogPopupCloseButton.addEventListener("click",function(o){o.preventDefault(),catalogBuySuccessPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(o){"Esc"!==o.key&&"Escape"!==o.key||catalogBuySuccessPopup.classList.contains("modal-show")&&(o.preventDefault(),catalogBuySuccessPopup.classList.remove("modal-show"))});
