const slider=document.querySelector(".slider"),sliderElements=slider.querySelectorAll(".slider-element"),sliderDots=slider.querySelectorAll(".slider-dot"),sliderNextButton=slider.querySelector(".slider-arrow-next"),sliderPrevButton=slider.querySelector(".slider-arrow-back");let currentSlideIndex=0;const makeElementActive=function(e){for(let e=0;e<sliderElements.length;e++)sliderElements[e].classList.remove("slider-element-active");sliderElements[e].classList.add("slider-element-active"),currentSlideIndex=e;for(let e=0;e<sliderDots.length;e++)sliderDots[e].classList.remove("slider-dot-active");sliderDots[e].classList.add("slider-dot-active")},addDotClickHandler=function(e){sliderDots[e].addEventListener("click",()=>makeElementActive(e))};for(let e=0;e<sliderDots.length;e++)addDotClickHandler(e);sliderNextButton.addEventListener("click",function(){const e=sliderElements.length;makeElementActive((currentSlideIndex+1)%e)}),sliderPrevButton.addEventListener("click",function(){const e=sliderElements.length;makeElementActive((e+currentSlideIndex-1)%e)});
